import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const greetDiv = {
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	height: '400px',
	width: '100%',
};

let timeDate = new Date().getHours();
let greet = '';
let styleName = '';
// console.log(timeDate);
if (timeDate < 12) {
	greet = 'Good Afternoon';
  const afternoonStyle = {
    color:'green',
  }
  styleName = afternoonStyle;
} else {
	greet = 'Good Morning';
  const morningStyle = {
    color: 'black',
  }
  styleName = morningStyle;
}

ReactDOM.render(
	<>
		<div style={greetDiv}>
			<h1 className='text'>
				Hello Sir, <span style={{styleName}}>{greet}</span>
			</h1>
		</div>
	</>,
	document.getElementById('root')
);
