import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const greetDiv = {
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	height: '400px',
	width: '100%',
};

let timeDate = new Date().getHours();
let greet = '';
// console.log(timeDate);
if (timeDate > 12) {
	greet = 'Good Afternoon';
} else {
	greet = 'Good Morning';
}

ReactDOM.render(
	<>
		<div style={greetDiv}>
			<h1 className='text'>
				Hello Sir,<span className='text2'>{greet}</span>
			</h1>
		</div>
	</>,
	document.getElementById('root')
);
